function myModal(){
                                            document.getElementById('myModal4').style.display='block';
                                            var img3 = document.createElement('img');
                                            img3.src = 'data:image/png;base64,{{practicefiles.datas}}';
                                            img3.style.margin = 'auto';
                                            img3.style.display = 'block';
                                            img3.style.width = '40%';
                                            img3.style.padding = '4%';
                                            img3.style.backgroundColor = '#ffffff';
                                            document.getElementById('myModal4').appendChild(img3);
                                            }; myModal();

