      function namecombi(){
          var fullPath = document.getElementById('x_nrc_photo_front').value;
          var image = document.getElementById('x_nrc_photo_front');
          if(event.target.files.length > 0){
          	image.src = URL.createObjectURL(event.target.files[0]);
        	  document.getElementById('popf').src = image.src;
          	}
          if (fullPath) {
           var startIndex = (fullPath.indexOf('\\') >= 0 ? fullPath.lastIndexOf('\\') : fullPath.lastIndexOf('/'));
           var filename = fullPath.substring(startIndex);
           if (filename.indexOf('\\') === 0 || filename.indexOf('/') === 0) {
           filename = filename.substring(1);
         var name = filename.substr(0, filename.lastIndexOf('.'));
         var date = new Date();
         dateString = date.toISOString().split('T')[0];
         var extension = filename.split('.').pop();
         var localeSpecificTime = date.toLocaleTimeString();
}
$( "#show_nrc_front_name" ).html( name+dateString+"-" +date.getHours() +":"+ date.getMinutes()+(Math.floor(Math.random()*90000) + 10000)+"."+extension );
 document.getElementById('x_nrc_photo_front_name').value= name+dateString+"-" +date.getHours() +":"+ date.getMinutes()+(Math.floor(Math.random()*90000) + 10000)+"."+extension ;   
} 
}

