         function FileDetails() {
              var fi = document.getElementById('x_academic');
              fi.src = URL.createObjectURL(event.target.files[0]);
             document.getElementById('savepop').src = fi.src;
              var date = new Date();
              dateString = date.toISOString().split('T')[0];
              var localeSpecificTime = date.toLocaleTimeString();

              if (fi.files.length > 0) {
                  show_academic_qualification_name.value = '';
                  for (var i = 0; i <= fi.files.length - 1; i++) {

                      var fname = fi.files.item(i).name;      // THE NAME OF THE FILE.
                      var fsize = fi.files.item(i).size;      // THE SIZE OF THE FILE.
                      var fdata = fi.files.item(i).datas;


                      document.getElementById('show_academic_qualification_name').innerHTML =
                          document.getElementById('show_academic_qualification_name').innerHTML  +
                            fname.substr(0, fname.lastIndexOf('.'))+dateString+"-" +date.getHours() +":"+ date.getMinutes()+(Math.floor(Math.random()*90000) + 10000)+"."+fname.split('.').pop() + '
' ;
                  }
              }
          }

   function PracticeMulti() {
        var fi = document.getElementById('x_practice');
        fi.src = URL.createObjectURL(event.target.files[0]);
       document.getElementById('savepop').src = fi.src;
        var date = new Date();
        dateString = date.toISOString().split('T')[0];
        var localeSpecificTime = date.toLocaleTimeString();

        if (fi.files.length > 0) {
            show_practice_multi_filename.value = '';
            for (var i = 0; i <= fi.files.length - 1; i++) {

                var fname = fi.files.item(i).name;      // THE NAME OF THE FILE.
                var fsize = fi.files.item(i).size;      // THE SIZE OF THE FILE.
                var fdata = fi.files.item(i).datas;


                document.getElementById('show_practice_multi_filename').innerHTML =
                    document.getElementById('show_practice_multi_filename').innerHTML  +
                      fname.substr(0, fname.lastIndexOf('.'))+dateString+"-" +date.getHours() +":"+ date.getMinutes()+(Math.floor(Math.random()*90000) + 10000)+"."+fname.split('.').pop() + '
' ;
            }
        }
    }

function dropDown(event) {

    document.getElementById('selist').classList.remove('d-none');
    document.getElementById("overlay").classList.remove("d-none");

}

function hide(event) {
    var items = document.getElementsByClassName('menu');
    for (let i = 0; i < items.length; i++) {
        items[i].classList.add("d-none");
    }
    document.getElementById("overlay").classList.add("d-none");

}

