var modalod1 = document.getElementById("popod1");
var closeod1 = document.getElementById("closeod1");
  // Get the image and insert it inside the modal - use its "alt" text as a caption
  var imgod1 = document.getElementById("otherdocument1");
  var image = document.getElementById("x_other_attachment_1");
  var modalImgod1 = document.getElementById("otherdoc1");
  var captionText = document.getElementById("caption");
  imgod1.onclick = function(){
    modalod1.style.display = "block";
    modalImgod1.src = imgod1.src;
    captionText.innerHTML = this.alt;
  }
    // Get the element that closes the modal
  var span = document.getElementsByClassName("close")[0];

  // When the user clicks on  (x), close the modal
  closeod1.onclick = function() { 
    modalod1.style.display = "none";
  }

