function myModal(){
                  document.getElementById('myModalmpbia').style.display='block';
                  var img3 = document.createElement('img');
                  img3.src = 'data:image/png;base64,{{mpbiafiles.datas}}';
                  img3.style.margin = 'auto';
                  img3.style.display = 'block';
                  img3.style.width = '40%';
                  img3.style.padding = '4%';
                  img3.style.backgroundColor = '#ffffff';
                  document.getElementById('myModalmpbia').appendChild(img3);
                  }; myModal();

