function myClick(){
          var date1 = document.getElementById('date7').value;
          if (date1 == '')
          {

          return false;
          }
          else {
          var dd = date1.substring(0, 2);
          var mm = date1.substring(3,5);
          var yy = date1.substring(6,10);
          var date2 = mm + '/' +dd+ '/' +yy

          if(!/^\d{1,2}\/\d{1,2}\/\d{4}$/.test(date2)) 
          {   

          return false;
          }
          else{

          var test = new Date(date2);
          var year = test.getFullYear();
          var fyear = year + 1;
          var fd = 01 + '/' + 01 + '/' +fyear

          var lmonth = ('0' + (test.getMonth() + 1)).slice(-2);
          var day = ('0' + (test.getDate()+1)).slice(-2);
          var sd =   lmonth + '/' +day + '/' +  year;
          var ssd = new Date(sd)
          var sdate = ssd.toISOString().slice(0,10);
          document.getElementById('date3').value = sdate;

          var ydate = new Date(fyear,0,2);
          var enddate = ydate.toISOString().slice(0,10);
          document.getElementById('date4').value = enddate;

          var yd = new Date(fyear,0,1);
          var d = ('0' + yd.getDate()).slice(-2);
          var m = ('0' + (yd.getMonth() + 1)).slice(-2);
          var y = ydate.getFullYear();
          var edate = d + '/' + m + '/' + y
          document.getElementById('date2').value = edate;




          return true;
          }
          }
          }

