function namecombinepass(){
var fullPath = document.getElementById('x_passportattachment').value;
var image = document.getElementById('x_passportattachment');
if(event.target.files.length > 0){
image.src = URL.createObjectURL(event.target.files[0]);
document.getElementById('otherdocumentpass').src = image.src;
}
if (fullPath) {
var startIndex = (fullPath.indexOf('\\') >= 0 ? fullPath.lastIndexOf('\\') : fullPath.lastIndexOf('/'));
var filename = fullPath.substring(startIndex);
if (filename.indexOf('\\') === 0 || filename.indexOf('/') === 0) {
filename = filename.substring(1);
var name = filename.substr(0, filename.lastIndexOf('.'));
var date = new Date();
dateString = date.toISOString().split('T')[0];
var extension = filename.split('.').pop();
var localeSpecificTime = date.toLocaleTimeString();
}
$( '#show_passport_attachment' ).html( name+dateString+'-' +date.getHours() +':'+ date.getMinutes()+(Math.floor(Math.random()*90000) + 10000)+'.'+extension );
document.getElementById('x_passportattachment_filename').value= name+dateString+'-' +date.getHours() +':'+ date.getMinutes()+(Math.floor(Math.random()*90000) + 10000)+'.'+extension ; 
show_pass_hide();                  
} 
}

   function SubmitFileDetails() {
   var fi = document.getElementById('x_academic');
   var date = new Date();
   dateString = date.toISOString().split('T')[0];
   var localeSpecificTime = date.toLocaleTimeString();

   if (fi.files.length > 0) {
   show_academic_qualification_name.value = '';

   for (var i = 0; i <= fi.files.length - 1; i++) {

   var fname = fi.files.item(i).name;      // THE NAME OF THE FILE.
   var fsize = fi.files.item(i).size;      // THE SIZE OF THE FILE.
   var fdata = fi.files.item(i).datas;

   document.getElementById('show_academic_qualification_name').innerHTML =
   document.getElementById('show_academic_qualification_name').innerHTML  +
   fname.substr(0, fname.lastIndexOf('.'))+dateString+"-" +date.getHours() +":"+ date.getMinutes()+(Math.floor(Math.random()*90000) + 10000)+"."+fname.split('.').pop() + '
' ;
   }
   }
   show_academic_files();
   }

